footer_js:
(function($) {
 "use strict";
 
	$(".page-content .products-grid.grid-items .product-item").each(function(idx){
 if(idx == 0 || idx == 7 || idx == 10){
 $(this).addClass("width2x");
 }
 });
 $(".page-content .products-grid.grid-items").isotope({
 itemSelector : ".product-item",
 layoutMode: "packery"
 });
 
})(jQuery);

custom_css:
.site-header-inner {border-bottom: 1px solid #e5e7eb;}.widget_product_categories ul.product-categories ul {display: none;}.widget_product_categories > ul > li{margin-bottom:0}.products-grid.grid-items .grid-item{width: 25%}.products-grid.grid-items .grid-item.width2x{width: 50%}.page-content .products-grid.grid-items .product-item .item-inner{margin-bottom: 40px}
.page-content .products-grid.grid-items .product-item.width2x .item-inner{ margin-bottom: 90px}
.page-content .products-grid.grid-items .product-item .item-inner:hover:before{ bottom: -20px }
.page-content .products-grid.grid-items .product-item.width2x .item-inner:hover:before{ bottom: -50px }
